import { nanoid } from 'nanoid';
import { getDatabase, child, ref, set, get } from 'firebase/database'
import { isWebUrl } from 'valid-url';



function InputURL({setShortLink}) {
    var longURL = ''
    const setValue = (value) =>{
        longURL = value
    }

    const handleClick = () =>{
        setShortLink(generateShortLink(longURL));
        console.log(longURL);
        longURL = ''

    }

    function generateShortLink(){
        var generatedKey = nanoid(5);
        var generateURL = "shortify.com/" + generatedKey;

        const db = getDatabase();
        set(ref(db,'/',generatedKey),{
            generatedKey : generatedKey,
            longURL: longURL,
            generateURL:generateURL
        }) .then((result) => {
            this.setState({
                generateURL:generateURL,
            })
        })
    }

    return (
        <div className='inputContainer'>
            <h1>URL <span>Shortener</span></h1>
            <div class="container">
                <input 
                id="input"
                type="input" 
                class="inputfield" 
                placeholder="Your URL" 
                onChange={e => setValue(e.target.value)}
                />
                <button onClick = {handleClick} type="button" class="btn">Submit</button>
            </div>
        </div>
    )
}

export default InputURL
